<?php
    /**
        * @package SVG_TO_PNG_FEATURED_IMG
        * @version 0.1
    */
    /*
        Plugin Name: SVG Featured Image
        Plugin URI: http://wordpress.org/plugins/hello-dolly/
        Description: Automatic converter for featured images on wordpress that uses SVG image format. This plugin automatically creates an equivalent PNG format & adds to the meta tags in header.
        Author: Niranjan Kumar A.K.A NiRUS
        Version: 0.1
        Author URI: http://nirus.io
    */


include plugin_dir_path(__FILE__) . 'post-watch.php';

$icon = 'data:image/svg+xml;base64,';
// create custom plugin settings menu
add_action('admin_menu', 'svg2png_plugin_menu');

function svg2png_plugin_menu() {

    global $icon;
    add_menu_page(
        'SVG to PNG', 
        'SVG &#8594; PNG', 
        'administrator', 
        __FILE__, 
        'svg_png_plugged', 
        $icon
    );

	//call register settings function
    add_action( 'admin_init', 'register_plugin_settings' );
    wp_register_style( 'prefix-style', plugins_url('style.css', __FILE__) );
    wp_enqueue_style( 'prefix-style' );
    
}

function register_plugin_settings() {
    //register our settings
    
    add_settings_field("SVG_PNG_FB_OPTION", "FB option", "svg_png_plugged", "svg-png-settings-group");  
    add_settings_field("SVG_PNG_TWT_OPTION", "Twitter option", "svg_png_plugged", "svg-png-settings-group"); 

    register_setting("svg-png-settings-group", "SVG_PNG_FB_OPTION");
    register_setting("svg-png-settings-group", "SVG_PNG_TWT_OPTION");

	register_setting( 'svg-png-settings-group', 'SVG_PNG_FOLDER_PATH', 'create_folder');
}


function create_folder($option) {
    $upload = wp_upload_dir();
    $upload_dir = $upload['basedir'];
    $upload_dir = $upload_dir . '/' . $option;
    if (! is_dir($upload_dir)) {
       mkdir( $upload_dir, 0775 );
    }

    return $option;
}

function svg_png_plugged() {
    return load_template(dirname( __FILE__ ) . '/settings-view.php', true);
}
?>